mapper.insert:
INSERT INTO tb_reply(
	reply_no
	, article_no
	, reply_text
	, reply_writer
	, reg_date
	, update_date
)VALUES(
	seq_reply_no.NEXTVAL
	, #{article_no}
	, #{reply_text}
	, #{reply_writer}
	, SYSDATE
	, SYSDATE
)

mapper.update:
UPDATE tb_reply
SET
	reply_text = #{reply_text}
	, update_date = SYSDATE
WHERE reply_no = #{reply_no}
