mapper.insert:
INSERT INTO tb_article(
	article_no
	, title
	, content
	, writer
	, reg_date
	, viewcnt
) VALUES(
	seq_article_no.NEXTVAL
	, #{title}
	, #{content}
	, #{writer}
	, SYSDATE
	, #{viewCnt}
)

mapper.update:
UPDATE tb_article
SET
	title = #{title}
	, content = #{content}
WHERE article_no = #{article_no}

UPDATE tb_article
SET replycnt = replycnt + #{amount}
WHERE article_no = #{article_no}

UPDATE tb_article
SET viewcnt = viewcnt + 1
WHERE article_no = #{article_no}
